function formatDateToComparable(dateStr) {
  if (!dateStr) return "";

  // Check if it's already in ISO format (YYYY-MM-DD or ISO string)
  if (/^\d{4}-\d{2}-\d{2}/.test(dateStr)) {
    return dateStr.substring(0, 10); // Just use YYYY-MM-DD portion
  }

  // Otherwise, assume DD-MMM-YY
  const monthMap = {
    Jan: "01", Feb: "02", Mar: "03", Apr: "04", May: "05", Jun: "06",
    Jul: "07", Aug: "08", Sep: "09", Oct: "10", Nov: "11", Dec: "12"
  };

  const parts = dateStr.split("-");
  if (parts.length !== 3) return "";

  const [day, mon, yearSuffix] = parts;
  const year = parseInt(yearSuffix, 10) < 50 ? `20${yearSuffix}` : `19${yearSuffix}`;
  return `${year}-${monthMap[mon]}-${day.padStart(2, '0')}`;
}

function formatDateOnly(dateStr) {
  if (!dateStr || typeof dateStr !== "string") return "N/A";

  const date = new Date(dateStr);
  if (isNaN(date)) return "Invalid Date";

  const day = String(date.getDate()).padStart(2, "0");
  const monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun",
                      "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
  const month = monthNames[date.getMonth()];
  const year = date.getFullYear();

  return `${day} ${month} ${year}`;
}

function convertUTMToLatLonSmart(easting, northing) {
  // Candidate UTM zones to test (default is 20N, but test 19 and 21 too for offshore points)
  const zonesToTry = [20, 19, 21];

  const wgs84 = "+proj=longlat +datum=WGS84 +no_defs";

  // A broad "Trinidad & Tobago region" bounding box (safe buffer)
  // This is intentionally wide to avoid excluding valid offshore points.
  const TT_BOUNDS = {
    minLat: 9.0,
    maxLat: 12.5,
    minLon: -63.5,
    maxLon: -59.0
  };

  // A rough "center point" for scoring (Port of Spain-ish)
  const TT_CENTER = { lat: 10.65, lon: -61.35 };

  function isInBounds(lat, lon) {
    return (
      lat >= TT_BOUNDS.minLat &&
      lat <= TT_BOUNDS.maxLat &&
      lon >= TT_BOUNDS.minLon &&
      lon <= TT_BOUNDS.maxLon
    );
  }

  function distanceScore(lat, lon) {
    // Simple distance heuristic (not true geodesic but good enough for scoring zones)
    const dLat = lat - TT_CENTER.lat;
    const dLon = lon - TT_CENTER.lon;
    return Math.sqrt(dLat * dLat + dLon * dLon);
  }

  let bestCandidate = null;

  for (const zone of zonesToTry) {
    const utmProj = `+proj=utm +zone=${zone} +datum=WGS84 +units=m +no_defs`;

    try {
      const [lon, lat] = proj4(utmProj, wgs84, [easting, northing]);

      // Candidate structure
      const candidate = {
        zone,
        lat,
        lon,
        inBounds: isInBounds(lat, lon),
        score: distanceScore(lat, lon)
      };

      // Prefer candidates inside bounds
      if (candidate.inBounds) {
        if (!bestCandidate || !bestCandidate.inBounds || candidate.score < bestCandidate.score) {
          bestCandidate = candidate;
        }
      } else {
        // If nothing is in bounds, keep the "least bad" option
        if (!bestCandidate) {
          bestCandidate = candidate;
        } else if (!bestCandidate.inBounds && candidate.score < bestCandidate.score) {
          bestCandidate = candidate;
        }
      }
    } catch (err) {
      console.warn(`UTM conversion failed for Zone ${zone}`, err);
    }
  }

  if (!bestCandidate) {
    // Absolute fallback (should never happen unless inputs are invalid)
    return [null, null];
  }

  // Return format matches your existing usage: [lat, lon]
  return [bestCandidate.lat, bestCandidate.lon];
}

function convertLatLonToUTM(lat, lon) {
  const proj4UTM = "+proj=utm +zone=20 +datum=WGS84 +units=m +no_defs";
  const [easting, northing] = proj4("EPSG:4326", proj4UTM, [lon, lat]);
  return { easting, northing };
}

function getStatusBadge(status) {
  const statusClass = {
    "Application Withdrawn": "status-withdrawn",
    "File Closed": "status-closed",
    "Issued CEC": "status-issued",
    "No CEC Required": "status-lime",
    "Notice of Refusal": "status-refused",
    "Pending": "status-pending",
    "General Letter": "status-letter"
  };

  const className = statusClass[status] || "status-pending"; // fallback
  return `<span class="status-badge ${className}">${status}</span>`;
}

function populateDropdown(selectId, items) {
  const select = document.getElementById(selectId);
  select.innerHTML = '<option value="">-- All --</option>'; // Default option

  items.forEach(value => {
    const option = document.createElement('option');
    option.value = value;
    option.textContent = value;
    select.appendChild(option);
  });
}

